import { useMemo, Fragment, forwardRef } from "react";
import {
  Box,
  Title,
  Text,
  Card,
  rem,
  Tooltip,
  Grid,
  Alert,
  Button,
  Badge,
  ActionIcon,
  Container,
} from "@mantine/core";
import { useBreedImageUrl, useBreed } from "../APIs/cats";
import { BsWikipedia, BsFillHeartFill } from "react-icons/bs";
import { BiHomeSmile } from "react-icons/bi";
import { MdArrowBack, MdChildFriendly, MdEnergySavingsLeaf } from "react-icons/md";
import { FaDog, FaCat, FaSmileBeam } from "react-icons/fa";
import { RiKakaoTalkFill } from "react-icons/ri";
import { IoPeople } from "react-icons/io5";
import { GiHairStrands, GiComb, GiHealthNormal, GiBrain } from "react-icons/gi";
import { useParams, useNavigate } from "react-router-dom";
import { BreedKnownFors } from "./breedKnownFors";

export function CatBreed() {
  const { breedId } = useParams();
  const { breed, error } = useBreed(breedId);
  const imageUrl = useBreedImageUrl(breedId);
  const navigate = useNavigate();

  const goBack = () => {
    navigate(-1);
  };

  const learnMoreLinks = (
    <Card sx={{ display: 'flex', alignItems: 'center', gap: rem(16), justifyContent: 'center' }}>
      <IconLink Icon={BsWikipedia} href={breed.wikipedia_url} label="Learn more on Wikipedia" />
      <IconLink Icon={RiKakaoTalkFill} href={breed.vetstreet_url} label="Learn more on Vetstreet" />
      <IconLink Icon={FaSmileBeam} href={breed.vcahospitals_url} label="Learn more on VCA Hospitals" />
      <IconLink Icon={FaSmileBeam} href={breed.cfa_url} label="Learn more on CFA" />
    </Card>
  )

  const knownFor = (
    <Card>
      <Grid>
        <Grid.Col>
          <BreedKnownFors breed={breed} />
        </Grid.Col>
      </Grid>
    </Card>
  )

  const description = (
    <Card bg='green' sx={{ display: 'flex', flexDirection: 'column', gap: rem(16), padding: rem(32) }}>
      {
        breed?.description && <Box>
          <Text size='xs' transform='uppercase'>Description</Text>
          <Text size='md' italic weight='normal' transform="capitalize">{breed?.description}</Text>
        </Box>
      }
      {
        breed?.alt_names && <Box>
          <Text size='xs' transform='uppercase'>Also known as</Text>
          <Text size='md' italic weight='normal' transform="capitalize">{breed?.alt_names}</Text>
        </Box>
      }
      {
        breed?.alt_names && <Box>
          <Text size='xs' transform='uppercase'>Temperament</Text>
          <Text size='md' italic weight='normal'>{breed?.temperament}</Text>
        </Box>
      }
      <Box>
        <Box>
          <Text size='xs' transform='uppercase'>Life Span</Text>
          <Text size='md' italic weight='normal'>{breed?.life_span} years</Text>
        </Box>
      </Box>

    </Card>
  )

  return (
    <>
      <Box
        p={rem(16)}
        sx={{
          width: '100%',
          boxSizing: 'border-box'
        }}
      >
        <Button variant='outline' leftIcon={<MdArrowBack />} onClick={goBack}>Back</Button>
      </Box>
      <Box
        sx={{
          width: "100%",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          paddingTop: rem(16),
          paddingBottom: rem(16),
        }}
      >
        {error && <Alert title="Failed to load breed!">{error}</Alert>}
        {breed && (
          <Container maxWidth='md'>
            <Card
              bg='blue'
              sx={{
                width: '100%',
                alignItems: 'start',
                maxWidth: rem(400),
              }}
            >
              <Box
                sx={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: rem(8),
                  paddingBottom: rem(16),
                }}
              >
                <Title>{breed?.name}</Title>
                <Tooltip label={breed.origin}>
                  <NationalFlag countryCode={breed?.country_code} />
                </Tooltip>
              </Box>
              <Card.Section>
                <Box sx={{
                  position: 'relative',
                  width: '100%',
                  height: 'auto',
                  aspectRatio: '4/3',
                }}>
                  {imageUrl && (
                    <img
                      src={imageUrl}
                      alt={`Image of ${breed.name} cat.`}
                      style={{
                        objectFit: "cover",
                        height: '100%',
                        width: '100%',
                        position: 'absolute',
                      }}
                    />
                  )}
                  <Badge sx={{ position: 'absolute', top: 0, left: 0, margin: `${rem(8)} ${rem(8)}` }}>
                    {breed.indoor === 1 ? 'Indoor' : 'Outdoor'}
                  </Badge>
                </Box>
              </Card.Section>
            </Card>
            {description}
            {knownFor}
            {learnMoreLinks}
          </Container>
        )}
      </Box>
    </>
  );
}

function IconLink({ Icon, href, label }) {
  return (
    <Tooltip label={label}>
      <ActionIcon
        variant="filled"
        component="a"
        radius={100}
        href={href}
        size='lg'
        sx={{
          textDecoration: "none",
        }}
      >
        <Icon fontSize={rem(18)} />
      </ActionIcon>
    </Tooltip>
  );
}

const NationalFlag = forwardRef(
  function NationalFlag({ countryCode }, ref) {
    // given a country code, return the flag as an unicode emoji
    const flag = useMemo(() => {
      switch (countryCode) {
        case 'AU':
          return '🇦🇺';
        case 'CA':
          return '🇨🇦';
        case 'CN':
          return '🇨🇳';
        case 'DK':
          return '🇩🇰';
        case 'FR':
          return '🇫🇷';
        case 'DE':
          return '🇩🇪';
        case 'IT':
          return '🇮🇹';
        case 'JP':
          return '🇯🇵';
        case 'KR':
          return '🇰🇷';
        case 'NL':
          return '🇳🇱';
        case 'NZ':
          return '🇳🇿';
        case 'NO':
          return '🇳🇴';
        case 'ES':
          return '🇪🇸';
        case 'SE':
          return '🇸🇪';
        case 'TR':
          return '🇹🇷';
        case 'GB':
          return '🇬🇧';
        case 'US':
          return '🇺🇸';
        default:
          return countryCode;
      }
    }, [countryCode]);
    return <Text ref={ref}>{flag}</Text>
  }
);
