import { useMemo, Fragment } from "react";
import {
  Box,
  Title,
  Text,
  Card,
  rem,
  Tooltip,
  Grid,
  Alert,
  Button,
  Badge,
} from "@mantine/core";
import { useBreedImageUrl, useBreed } from "../APIs/cats";
import { BsWikipedia, BsFillHeartFill } from "react-icons/bs";
import { BiHomeSmile } from "react-icons/bi";
import { MdArrowBack, MdChildFriendly, MdEnergySavingsLeaf } from "react-icons/md";
import { FaDog, FaCat, FaSmileBeam } from "react-icons/fa";
import { RiKakaoTalkFill } from "react-icons/ri";
import { IoPeople } from "react-icons/io5";
import { GiHairStrands, GiComb, GiHealthNormal, GiBrain } from "react-icons/gi";
import { useParams, useNavigate } from "react-router-dom";
import { BreedKnownFors } from "./breedKnownFors";

export function CatBreed() {
  const { breedId } = useParams();
  const { breed, error } = useBreed(breedId);
  const imageUrl = useBreedImageUrl(breedId);
  const navigate = useNavigate();

  const goBack = () => {
    navigate(-1);
  };

  return (
    <>
      <Box
        p={rem(16)}
        sx={{
          width: '100%',
          boxSizing: 'border-box'
        }}
      >
        <Button variant='outline' leftIcon={<MdArrowBack />} onClick={goBack}>Back</Button>
      </Box>
      <Box
        sx={{
          width: "100%",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          paddingTop: rem(16),
          paddingBottom: rem(16),
        }}
      >
        {error && <Alert title="Failed to load breed!">{error}</Alert>}
        {breed && (
          <>
            <Card
              sx={{
                alignItems: 'start'
              }}
            >
              <Box p={rem(16)} sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                <Title>{breed?.name}</Title>
                {breed?.alt_names && <Title order={6} italic weight='normal' transform="capitalize">{breed?.alt_names}</Title>}
              </Box>
              {imageUrl && (
                <img
                  src={imageUrl}
                  alt={`Image of ${breed.name} cat.`}
                  height={350 * 9 / 16}
                  width={350}
                  style={{
                    objectFit: "cover",
                    border: '2px solid black'
                  }}
                />
              )}
            </Card>
            <Card>
              <Text>{breed.description}</Text>
            </Card>
            <Card>
              <Text>{breed.temperament}</Text>
            </Card>

            <BreedKnownFors breed={breed} />
            <WikipediaLink breed={breed} />
          </>
        )}
      </Box>
    </>
  );
}

function WikipediaLink({ breed }) {
  return (
    <Box
      component="a"
      href={breed.wikipedia_url}
      sx={{
        display: "flex",
        alignItems: "center",
        gap: rem(8),
        textDecoration: "none",
      }}
    >
      <BsWikipedia />
      <Text size='sm'>More Information</Text>
    </Box>
  );
}
