        case 'CN':
          return 'ðŸ‡¨ðŸ‡³';
        case 'DK':
          return 'ðŸ‡©ðŸ‡°';
        case 'FR':
          return 'ðŸ‡«ðŸ‡·';
        case 'DE':
          return 'ðŸ‡©ðŸ‡ª';
        case 'IT':
          return 'ðŸ‡®ðŸ‡¹';
        case 'JP':
          return 'ðŸ‡¯ðŸ‡µ';
        case 'KR':
          return 'ðŸ‡°ðŸ‡·';
        case 'NL':
          return 'ðŸ‡³ðŸ‡±';
        case 'NZ':
          return 'ðŸ‡³ðŸ‡¿';
        case 'NO':
          return 'ðŸ‡³ðŸ‡´';
        case 'ES':
          return 'ðŸ‡ªðŸ‡¸';
        case 'SE':
          return 'ðŸ‡¸ðŸ‡ª';
        case 'TR':
          return 'ðŸ‡¹ðŸ‡·';
        case 'GB':
          return 'ðŸ‡¬ðŸ‡§';
        case 'US':
          return 'ðŸ‡ºðŸ‡¸';
        default:
          return countryCode;
      }
    }, [countryCode]);
    return <Text ref={ref}>{flag}</Text>
  }
);
